#!/usr/bin/env node

// npx is only used by `npm init`.
const isNpm = process.argv[1].includes('npx');

let args = process.argv.slice(2);
if (isNpm && args.length) {
  const isFlag = args[0].startsWith('-');
  args.splice(isFlag ? 0 : 1, 0, '--');
}

const command = isNpm
  ? 'npm init @angular@latest'
  : 'yarn create @angular';

args.unshift(command);

console.log('')
console.error(`Error: Did you mean to run "${args.join(' ')}"?`);
console.log('')

process.exitCode = 1;
